mapper.insert:

INSERT INTO haw.member (name, email, password, nickname, ip)
VALUES (
	   #{name}
	 , #{email}
	 , #{password}
	 , #{nickname}
	 , #{ip}
)


INSERT INTO haw.signin (member_no, email, signin_yn, ip)
VALUES (
	   #{member_no}
	 , #{email}
	 , #{signin_yn}
	 , #{ip}
)

mapper.update:

INSERT INTO haw.member_team (
	   member_no, team_no
) VALUES (
	   #{member_no}, #{team_no}
)


UPDATE haw.team SET
       team_cnt = team_cnt + 1
 WHERE team_no = #{team_no}
